-- create a new user and password with the neccessary permissions
CREATE USER IF NOT EXISTS 'dev_test'@'localhost'
IDENTIFIED BY 'dev_pass';
GRANT SELECT ON powerpulse_dev_test.*
TO 'dev_test'@'localhost';
FLUSH PRIVILEGES;

